import { useState, useEffect } from 'react'
import axios from 'axios'

function App() {

  const [rawData, setRawData] = useState([])
  const [countries, setCountries] = useState([])
  const [countriesToShow, setCountriesToShow] = useState([])


  useEffect(() => {
    console.log('sending request')
    axios
      .get('https://restcountries.com/v3.1/all')
      .then(response => {
        console.log('promise fulfilled')
        setRawData(response.data)
        })
    }, [])
  console.log('output API call:', rawData)
  console.log('output countries array:', countries)

  const _countries = [
    rawData[0].name
  ]

  setCountries[_countries]

  // useEffect(() => {
  //   console.log('useEffect countries array is being called.')
  //   rawData.map(
  //     country => countries.concat({
  //       name: country.name.common 
  //       // capital: country.capital[0]
  //     })
  //   )
  // }, [rawData])
  // console.log('output countries array:', countries)

  const handleFilterChange = (event) => {

    let _filteredCountries = countries.filter(country => 
      country.name.common.toLowerCase().includes(event.target.value.toLowerCase())
    )
    setCountriesToShow(_filteredCountries)
  }


  return (
    <div>
      <h2>Data for Countries</h2>
      <form >
            <div>
                Filter: <input onChange={handleFilterChange} />
            </div>
        </form>
      <h2>Data</h2>
        {countriesToShow}
    </div>
  )
}

export default App;
