import { useState, useEffect, useRef } from 'react'
import axios from 'axios'
import RenderContents from './components/renderContent'

function App() {

  // const [rawData, setRawData] = useState([])
  const [countries, setCountries] = useState([])
  const [rawData, setRawData] = useState([])
  const [countriesToShow, setCountriesToShow] = useState([])
  const [weatherData, setWeatherData] = useState([])
  const api_key = process.env.REACT_APP_API_KEY

  useEffect(() => {
    console.log('sending request restcountries')
    axios
      .get('https://restcountries.com/v3.1/all? ')
      .then(response => {        
        console.log('promise fulfilled')
        setRawData(response.data)
      })
    }, [])

  useEffect(() => {

    if (countriesToShow.length === 1) { 

      let country = countriesToShow[0]

      console.log(`https://api.openweathermap.org/data/3.0/onecall?lat={${country.capitalLatLng[0]}}&lon={${country.capitalLatLng[1]}}&exclude={alerts}&appid={${api_key}}`)
    }
    // axios
    //   .get(`https://api.openweathermap.org/data/3.0/onecall?lat={lat}&lon={lon}&exclude={alerts}&appid=${api_key}`)
    //   .then(response => {        
    //     console.log('promise fulfilled')
    //     setRawData(response.data)
    //   })
    }, [countriesToShow])

  useEffect(() => {

    let _countries = rawData.map(
      (item, i) => (
        {id: i, 
        name: item.name.common,
        capital: item.capital, 
        languages: item.languages,
        area: item.area,
        flag: item.flags.svg, 
        capitalLatLng: item.capitalInfo.latlng
      }
      )
    )
    setCountries(_countries)
  }, [rawData])

 // console.log('rawData output API call:', rawData)

  const handleFilterChange = (event) => {
    let _filteredCountries = countries.filter(country => 
      country.name.toLowerCase().includes(event.target.value.toLowerCase())
    )
    setCountriesToShow(_filteredCountries)
  }

  const handleSelect = (id) => {


    console.log("id is:", id)

    let _selectedCountry = countries.find(n => n.id === id)
    setCountriesToShow(_selectedCountry)

    console.log(_selectedCountry)

  }

  console.log('countriesToShow:', countriesToShow)

  return (
    <div>
      <h1>Data for Countries</h1>
      <form >
            <div>
                Filter by name: <input onChange={handleFilterChange} />
            </div>
        </form>
      <h1>Result:</h1>
      <RenderContents 
        countriesToShow = {countriesToShow} 
        handleSelect = {handleSelect} 
        /> 
    </div>
  )
}

export default App;
