import { useState, useEffect, useRef } from 'react'
import axios from 'axios'
import RenderContents from './components/renderContent'

function App() {

  // const [rawData, setRawData] = useState([])
  const [countries, setCountries] = useState([])
  const [rawData, setRawData] = useState([])
  const [countriesToShow, setCountriesToShow] = useState([])
  const inputRef = useRef(null);

  useEffect(() => {
    console.log('sending request')
    axios
      .get('https://restcountries.com/v3.1/all')
      .then(response => {        
        console.log('promise fulfilled')
        setRawData(response.data)
      })
    }, [])

  useEffect(() => {

    let _countries = rawData.map(
      item => (
        {id: item.name.official, 
        name: item.name.common,
        capital: item.capital, 
        languages: item.languages,
        area: item.area,
        flag: item.flags.svg
      }
      )
    )

    setCountries(_countries)
  }, [rawData])

 // console.log('rawData output API call:', rawData)

  const handleFilterChange = (event) => {
    let _filteredCountries = countries.filter(country => 
      country.name.toLowerCase().includes(event.target.value.toLowerCase())
    )
    setCountriesToShow(_filteredCountries)
  }

  const handleClick = () => {
    // event.preventDefault()

    // console.log('this is event.target.value:', event.target.value)

    setCountriesToShow(inputRef.current.value)

  }

  console.log('countriesToShow:', countriesToShow)

  return (
    <div>
      <h1>Data for Countries</h1>
      <form >
            <div>
                Filter by name: <input onChange={handleFilterChange} />
            </div>
        </form>
      <h1>Result:</h1>
      <RenderContents 
        countriesToShow = {countriesToShow} 
        inputRef = {inputRef}
        handleClick = {handleClick}
        /> 
    </div>
  )
}

export default App;
