const ListCountries = ({ countriesToShow, handleClick }) => {

    return (
        <div>
            <ul>    
            {countriesToShow.map(
                country => {
                    return ( 
                        <div key = {country.id} > 
                            <br/> 
                            {country.name} 
                            <button onClick={handleClick(country.id)}>show</button>
                        </div>
                    )
                }
            )}
            </ul>
        </div>
    )
}

export default ListCountries
