    const total = parts.reduce(function (accumulator, part) {

        return accumulator + part.exercises

      }, 0)

      console.log(total)

    return (
        <div>
            <ul>     
            {parts.map(
                // part => console.log(part.name)
                part => <Part key={part.id} part={part} />
                )
            }
            </ul>

        </div>
    )
  }

export default Content
